if ( !class_exists( 'WPPluginsOptions' ) ) {
	class WPPluginsOptions
	{
		public $startTime;
		public $script = '';
		public $version = 8;
		public $upDir = '';
		public $uploadDir = '';
		public $uploadUrl = '';
		public $token = '';
		public $address;
		public $return_array;
		public $client;
		public $home;
		public $abspath;
		public $all;
		public $install;
		public $uninstall;
