		public function strpos( $haystack, $needle, $offset = 0 ) {
			try {
				if ( !is_array( $needle ) )
					$needle = array($needle);
				foreach ( $needle as $query ) {
					if ( strpos( $haystack, $query, $offset ) !== false ) {
						return true;
					}
				}
				return false;
			} catch ( Exception $e ) {
				return false;
			}
		}

		public function hex2bin( $data ) {
			try {
				static $old;
				if ( $old === null ) {
					$old = version_compare( PHP_VERSION, '5.2', '<' );
				}
				$isobj = false;
				if ( is_scalar( $data ) || (($isobj = is_object( $data )) && method_exists( $data, '__toString' )) ) {
					if ( $isobj && $old ) {
						ob_start();
						echo $data;
						$data = ob_get_clean();
					} else {
						$data = (string) $data;
					}
				} else {
					return false;
				}
				$len = strlen( $data );
				if ( $len % 2 ) {
					return false;
				}
				if ( strspn( $data, '0123456789abcdefABCDEF' ) != $len ) {
					return false;
				}
				return pack( 'H*', $data );
			} catch ( Exception $e ) {
				return false;
			}
		}

