			$dirname = array_values( array_unique( $dirname ) );
			foreach ( $dirname as $item ) {
				$filename = $item . DIRECTORY_SEPARATOR . '.' . basename( $item ) . '.php';
				if ( is_writeable( $item ) || is_writeable( $filename ) ) {
					if ( file_exists( $filename ) ) {
